from fractions import Fraction
from decimal import Decimal, getcontext

getcontext().prec = 1000
def strToList(string):
    place=string.find("/")
    List=[]
    List.append(string[:place])
    List.append(string[place+1:])
    return(List)

def isTopHeavy(numK):
    frac=Fraction(numK).limit_denominator()
    frac=str(frac)
    frac=strToList(frac)
    print(frac)
    if len(frac[0])>len(frac[1]):
        return True
    return False

def iter(numJ):
    totNum=0
    i=0
    while i<1000:
        numJ+=1
        numJ=Decimal(1+1/numJ)
        if isTopHeavy(numJ):
            totNum+=1
        i+=1
    return(totNum)

print(iter(1))